import React, { useState } from 'react'


function App() {
  const [values, setValues]=useState("")
  const [listItem, setListItem]=useState([])

  const addItem=()=>{

    if(!values){
      alert("please enter something")
    }

    const item={
      id:listItem.length+1,
      value: values
    }
    setListItem(pre=>[...pre, item])
    setValues("")

  }
  return (
    <div className='main'>
      <h1>To do List</h1>
      <div className='event'>
        <input type="text" placeholder='write something'  value={values} onChange={(e)=>setValues(e.target.value)}/>
        <button onClick={addItem}>Add</button>
      </div>
      <ul>
        {
          listItem.map((e,i)=>{
            <li></li>
          })
        }
      </ul>
    </div>
  )
}

export default App
